import sys
import base64

flag = ""
msg="flag is dbdctf{not_the_correct_flag}"
def decode(n):
    decoded_flag = base64.b64decode(flag).decode()
    for i in range(n):
        decoded_flag = base64.b64decode(decoded_flag).decode()
    if "dbd" not in decoded_flag:
        printa(msg)
    else:
        printa(decoded_flag)

def printa(text):
    for i in text:
        print(i, end='', flush=True)
        import time
        time.sleep(0.005)

n=int(input("Enter the number of times you want to decode the flag: "))
try:
    decode(n)
except:
    printa(msg)




